import javax.swing.*;import java.awt.*;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import java.sql.Connection;import java.sql.DriverManager;import java.sql.SQLException;public class DBConnectionPanel extends JFrame {    private JTextField serverField, portField, userField;    private JPasswordField passwordField;    private JButton connectButton;    public DBConnectionPanel() {        setTitle("Conectar a la Base de Datos");        setLayout(new GridLayout(5, 2));        serverField = new JTextField();        portField = new JTextField();        userField = new JTextField();        passwordField = new JPasswordField();        add(new JLabel("Servidor:"));        add(serverField);        add(new JLabel("Puerto:"));        add(portField);        add(new JLabel("Usuario:"));        add(userField);        add(new JLabel("Contraseña:"));        add(passwordField);        connectButton = new JButton("Conectar");        add(new JLabel());        add(connectButton);        connectButton.addActionListener(new ActionListener() {            @Override            public void actionPerformed(ActionEvent e) {                connectToDatabase();            }        });        setSize(300, 200);        setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);        setLocationRelativeTo(null);    }    private void connectToDatabase() {        String server = serverField.getText();        String port = portField.getText();        String user = userField.getText();        String password = new String(passwordField.getPassword());        if (server.isEmpty() || port.isEmpty() || user.isEmpty() || password.isEmpty()) {            JOptionPane.showMessageDialog(this, "Todos los campos son obligatorios.");            return;        }        try {            int portNumber = Integer.parseInt(port);            String url = "jdbc:mysql://" + server + ":" + portNumber + "/Northwind";            Connection connection = DriverManager.getConnection(url, user, password);            JOptionPane.showMessageDialog(this, "Conexión exitosa");            new ProductManagementPanel(connection).setVisible(true);            dispose();        } catch (NumberFormatException ex) {            JOptionPane.showMessageDialog(this, "El puerto debe ser un valor numérico.");        } catch (SQLException ex) {            JOptionPane.showMessageDialog(this, "Error al conectar: " + ex.getMessage());        }    }    public static void main(String[] args) {        SwingUtilities.invokeLater(() -> new DBConnectionPanel().setVisible(true));    }}